var listen = require("./addLoadListener.js");

module.exports = function(urls, cb) {
    var len = urls.length,
        values = Array(len);
    urls.forEach(function(url, index) {
        listen(url, function(value) {
            values[index] = value;
            if(!--len) {
                for(var i=0; i<values.length; i++)
                    if(values[i] instanceof Error) {
                        cb(values[i]);
                        return;
                    }
                cb(null, ...values);
            }
        });
    });
};
